// Basic Usage Example
const { createMonacoRecorder } = window.monacoRecorder;

// Create editor instance
const editor = monaco.editor.create(
  document.getElementById('editor'),
  { value: '// Start recording...\n' }
);

// Initialize recorder
const recorder = createMonacoRecorder(editor);

// Start recording
recorder.start();

