// React Integration Example
import { useEffect, useRef, useState } from 'react';
import * as monaco from 'monaco-editor';
import { createMonacoRecorder } from 'monaco-recorder';

function EditorWithRecorder() {
  const editorRef = useRef(null);
  const recorderRef = useRef(null);
  const [isRecording, setIsRecording] = useState(false);

  useEffect(() => {
    // Initialize editor
    const editor = monaco.editor.create(
      editorRef.current,
      { value: '// Start coding...\n' }
    );

    // Initialize recorder
    recorderRef.current = createMonacoRecorder(editor);

    return () => editor.dispose();
  }, []);

  const handleRecordClick = () => {
    if (isRecording) {
      recorderRef.current.stop();
    } else {
      recorderRef.current.start();
    }
    setIsRecording(!isRecording);
  };

  const handlePlay = async () => {
    try {
      await recorderRef.current.play();
    } catch (error) {
      console.error('Playback failed:', error);
    }
  };

  return (
    <div>
      <div ref={editorRef} style={{ height: '300px' }} />
      <div>
        <button
          onClick={handleRecordClick}
          style={{ background: isRecording ? '#ff4444' : '#4CAF50' }}
        >
          {isRecording ? '⏹ Stop Recording' : '⏺ Start Recording'}
        </button>
        <button onClick={handlePlay} disabled={isRecording}>
          ▶ Play Recording
        </button>
      </div>
    </div>
  );
}

